mapper.select:
SELECT COUNT(*)
FROM contract_matched_order
WHERE 1=1

    and contract_id=#{sourceId}


    and user_id=#{userId}


    and gmt_create >= #{startTime}


    and gmt_create < #{endTime}

SELECT *
FROM contract_matched_order
WHERE 1=1

    and contract_id=#{sourceId}


    and user_id=#{userId}


    and gmt_create >= #{startTime}


    and gmt_create < #{endTime}

order by gmt_create desc, id
limit #{startRow}, #{endRow}

