(ask_user_id=#{userId} or bid_user_id=#{userId})


    and asset_id in

        #{item}




    and gmt_create < #{endTime}


    and gmt_create >= #{startTime}

select

from usdk_matched_order
where  (ask_user_id=#{userId} or bid_user_id=#{userId})

    and asset_id in

        #{item}



    and gmt_create < #{endTime}


    and gmt_create >= #{startTime}



order by gmt_create DESC, id
