mapper.select:
select

from trade_usdk_order
where id  <=  #{contractOrderIndex, jdbcType=INTEGER}
and order_direction = #{orderDirection, jdbcType=INTEGER}
and status in (#{placeOrder, jdbcType=INTEGER}, #{partialSuccess, jdbcType=INTEGER})

select count(*)
from trade_usdk_order
where 1=1

  and asset_id=#{assetId,jdbcType=INTEGER}


  and user_id=#{userId,jdbcType=BIGINT}


  and gmt_create >= #{startTime}


  and gmt_create <= #{endTime}


  and status in

    #{item,jdbcType=INTEGER}

select

from trade_usdk_order
where 1=1

  and asset_id=#{assetId}


  and user_id=#{userId}


  and gmt_create >= #{startTime}


  and gmt_create <= #{endTime}


  and status in

    #{item,jdbcType=INTEGER}


order by gmt_create desc
limit #{startRow,jdbcType=INTEGER}, #{endRow,jdbcType=INTEGER}

mapper.update:
update trade_usdk_order
set gmt_modified = now(),
status = #{status}
where id = #{id} and user_id = #{userId}
